contracts/interfaces/IIssuerRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

/// @dev The status of the public key, only active public key can be used for verification.
enum PublicKeyStatus {
    UNINITIALIZED,
    REVOKED,
    ACTIVE
}

/// @dev The public key struct
struct PublicKey {
    /// @dev the verification stack id that the public key is enabled for.
    mapping(uint8 => bool) enabledVerificationStacks;
    /// @dev the status of the public key
    PublicKeyStatus status;
    /// @dev the raw bytes of the public key. Not used in the contract, only for off-chain usage.
    /// The spec for marshalling the public key is defined by the issuer.
    bytes raw;
}

/// @dev The revoked signature ID in a sparsed merkle tree.
struct SignatureState {
    /// @dev the root hash of the sparsed merkle tree.
    bytes32 root;
    /// @dev uri to the sparsed merkle tree, representing the revoked signature ids.
    string treeURI;
}

/// @dev The issuer struct
struct Issuer {
    /// @dev the name of the issuer
    string name;
    /// @dev the admin of the issuer, who can add or revoke public keys.
    address admin;
}

/// @title IIssuerRegistry
/// @dev The interface for the issuer registry contract.
interface IIssuerRegistry {
    /// @notice Emitted when a new issuer is registered in the registry.
    /// @param issuerId Unique identifier for the newly registered issuer.
    /// @param name Name of the issuer registered.
    event IssuerRegistered(uint256 indexed issuerId, string name);

    /// @notice Emitted when an issuer's administrative role is transferred to a new address.
    /// @param issuerId ID of issuer whose admin is being transferred.
    /// @param oldAdmin Address of the current admin being replaced.
    /// @param newAdmin Address of the new admin taking over.
    event IssuerAdminTransferred(uint256 indexed issuerId, address indexed oldAdmin, address indexed newAdmin);

    /// @notice Emitted when the status of a public key is updated.
    /// @param issuerId ID for the issuer to whom the public key belongs.
    /// @param publicKeyId ID for the public key being updated.
    /// @param status New status of the public key.
    event PublicKeyStatusUpdated(uint256 indexed issuerId, uint256 indexed publicKeyId, PublicKeyStatus status);

    /// @notice Emitted when the verification stack for a public key is updated.
    /// @param issuerId Unique identifier for the issuer to whom the public key belongs.
    /// @param publicKeyId Unique identifier for the public key.
    /// @param verificationStackId Identifier for the verification stack being updated.
    /// @param enabled Boolean indicating whether the stack is enabled or disabled for the public key.
    event PublicKeyVerificationStackUpdated(
        uint256 indexed issuerId,
        uint256 indexed publicKeyId,
        uint256 indexed verificationStackId,
        bool enabled
    );

    /// @notice Emitted when the signature state URI is updated for a specific type and context.
    /// @param typeId Type identifier for the signature state being updated.
    /// @param contextID Context identifier for the signature state being updated.
    /// @param issuerId Issuer identifier related to the signature state.
    /// @param newTreeURI New URI for the signature state tree.
    event SignatureStateURIUpdated(
        uint160 indexed typeId,
        uint160 indexed contextID,
        uint256 indexed issuerId,
        string newTreeURI
    );

    /// @notice Emitted when the signature state root is updated for a specific type and context.
    /// @param typeId Type identifier for the signature state being updated.
    /// @param contextID Context identifier for the signature state being updated.
    /// @param issuerId Issuer identifier related to the signature state.
    /// @param newRoot New root hash for the signature state tree.
    event SignatureStateRootUpdated(
        uint160 indexed typeId,
        uint160 indexed contextID,
        uint256 indexed issuerId,
        bytes32 newRoot
    );

    /// @dev Registers a new issuer along with their first public key and enabled verification stack.
    /// @param name Name of the issuer to register.
    /// @param verificationStackId Identifier for the verification stack.
    /// @param publicKeyId  Identifier for the issuer's public key.
    /// @param publicKeyRaw The raw public key data.
    /// @return The unique identifier for the newly registered issuer.
    function registerIssuer(
        string calldata name,
        uint8 verificationStackId,
        uint256 publicKeyId,
        bytes calldata publicKeyRaw
    ) external returns (uint256);

    /// @dev Transfers the administrative role of an issuer to a new owner.
    /// @param issuerId Unique identifier for the issuer.
    /// @param newOwner Address of the new administrator.
    function transferIssuerAdmin(uint256 issuerId, address newOwner) external;

    /// @dev Adds a new public key for an issuer and enables it for a specified verification stack.
    /// @param issuerId Unique identifier for the issuer.
    /// @param verificationStackId Identifier for the verification stack.
    /// @param publicKeyId Unique identifier for the new public key.
    /// @param publicKeyRaw The raw public key data.
    function addPublicKey(
        uint256 issuerId,
        uint8 verificationStackId,
        uint256 publicKeyId,
        bytes calldata publicKeyRaw
    ) external;

    /// @dev Updates the status (active/revoked) of an issuer's public key.
    /// @param issuerId Unique identifier for the issuer.
    /// @param publicKeyId Unique identifier for the public key.
    /// @param status New status for the public key.
    // only the issuer admin can call this function
    function updatePublicKeyStatus(uint256 issuerId, uint256 publicKeyId, PublicKeyStatus status) external;

    /// @dev Updates the verification stack association for an issuer's public key.
    /// @param issuerId Unique identifier for the issuer.
    /// @param publicKeyId Unique identifier for the public key.
    /// @param verificationStackId New verification stack identifier for the public key.
    /// @param enabled Boolean indicating whether the stack is enabled for the public key.
    function updatePublicKeyVerificationStack(
        uint256 issuerId,
        uint256 publicKeyId,
        uint8 verificationStackId,
        bool enabled
    ) external;

    /// @dev Updates the signature state URI for a given type and context.
    /// @param typeId Type identifier.
    /// @param contextId Context identifier.
    /// @param issuerId Unique identifier for the issuer.
    /// @param treeURI New URI for the signature state.
    function updateSignatureStateURI(
        uint160 typeId,
        uint160 contextId,
        uint256 issuerId,
        string calldata treeURI
    ) external;

    /// @dev Updates the signature SMT root for a given type, context and issuer.
    /// @param typeId Type identifier.
    /// @param contextId Context identifier.
    /// @param issuerId Unique identifier for the issuer.
    /// @param root New root hash for the revoked SMT.
    function updateSignatureState(uint160 typeId, uint160 contextId, uint256 issuerId, bytes32 root) external;

    /// @dev Sets both the signature SMT URI and root for a given type, context and issuer.
    /// @param typeId Type identifier.
    /// @param contextId Context identifier.
    /// @param issuerId Unique identifier for the issuer.
    /// @param treeURI New URI for the signature state.
    /// @param root New root hash for the signature state.
    function setSignatureState(
        uint160 typeId,
        uint160 contextId,
        uint256 issuerId,
        string calldata treeURI,
        bytes32 root
    ) external;

    /// @dev Retrieves the issuer data, reverts if the issuer does not exist.
    /// @param issuerId Unique identifier for the issuer.
    /// @return Issuer data structure containing issuer details.
    function getIssuer(uint256 issuerId) external view returns (Issuer memory);

    /// @dev Retrieves the raw public key data for an issuer's public key.
    /// @param issuerId Unique identifier for the issuer.
    /// @param publicKeyId Unique identifier for the public key.
    /// @return The raw public key data.
    function getPublicKeyRaw(uint256 issuerId, uint256 publicKeyId) external view returns (bytes memory);

    /// @dev Checks if an issuer's public key is active.
    /// @param issuerId Unique identifier for the issuer.
    /// @param publicKeyId Unique identifier for the public key.
    /// @return True if the public key is active, false otherwise.
    function isPublicKeyActive(uint256 issuerId, uint256 publicKeyId) external view returns (bool);

    /// @dev Checks if an issuer's public key is active and enabled for a specific verification stack.
    /// @param issuerId Unique identifier for the issuer.
    /// @param publicKeyId Unique identifier for the public key.
    /// @param verificationStackId Identifier for the verification stack.
    /// @return True if the public key is active and enabled for the stack, false otherwise.
    function isPublicKeyActiveForStack(
        uint256 issuerId,
        uint256 publicKeyId,
        uint8 verificationStackId
    ) external view returns (bool);

    /// @dev Retrieves the signature state for a given type, context and issuer.
    /// @param typeId Type identifier.
    /// @param contextId Context identifier.
    /// @param issuerId Unique identifier for the issuer.
    /// @return The signature state.
    function getSignatureState(
        uint160 typeId,
        uint160 contextId,
        uint256 issuerId
    ) external view returns (SignatureState memory);

    /// @dev Retrieves the signature state URI for a given type, context and issuer.
    /// @param typeId Type identifier.
    /// @param contextId Context identifier.
    /// @param issuerId Unique identifier for the issuer.
    /// @return The URI of the signature state.
    function getSignatureStateURI(
        uint160 typeId,
        uint160 contextId,
        uint256 issuerId
    ) external view returns (string memory);

    /// @dev Retrieves the signature state root for a given type, context and issuer.
    /// @param typeId Type identifier.
    /// @param contextId Context identifier.
    /// @param issuerId Unique identifier for the issuer.
    /// @return The root hash of the signature state.
    function getSignatureStateRoot(uint160 typeId, uint160 contextId, uint256 issuerId) external view returns (bytes32);
}

contracts/interfaces/IProofVerifier.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

/// @title IProofVerifier
/// @dev Interface for static verification of zero-knowledge proofs.
interface IProofVerifier {
    /// @notice Retrieves the verification keys.
    /// @dev This function is used to get the verification keys that are necessary for verifying proofs.
    /// @return An array of `uint` representing the verification keys.
    function getVerificationKeys() external view returns (uint256[] memory);

    /// @dev Checks if the public signals are aliased. Aliased signals should never be used in proofs.
    ///      This is useful when using proof aggregators that does not check for signal aliasing.
    /// @param _pubSignals An array of `uint` representing the public signals.
    /// @return A boolean value indicating if any public signal is aliased (`true`) or not (`false`).
    function isAliased(uint256[] calldata _pubSignals) external view returns (bool);

    /// @dev This function takes a cryptographic proof and public signals to verify the proof's validity.
    /// @notice It verifies the proof and check if public signals were aliased.
    /// @param _proofs An array of `uint` representing the proof.
    /// @param _pubSignals An array of `uint` representing the public signals.
    /// @return A boolean value indicating whether the proof is valid (`true`) or not (`false`).
    function verifyProof(uint256[] calldata _proofs, uint256[] calldata _pubSignals) external view returns (bool);
}

contracts/interfaces/IPublicSignalGetter.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

/// @title Intrinsic Signal enum
enum IntrinsicSignalName {
    TYPE,
    CONTEXT,
    NULLIFIER,
    EXTERNAL_NULLIFIER,
    REVEAL_IDENTITY,
    EXPIRATION_LB,
    KEY_ID,
    ID_EQUALS_TO,
    SIG_REVOCATION_SMT_ROOT
}

/// @title Public Signal Getter Interface
/// @dev Public signals (inputs) are represented as an array of uints in galxe identity protocol.
///      To find the public signal for a given signal name, type designer should set a contract
///      in the type registry that implements this interface.
interface IPublicSignalGetter {
    /// @dev get the public signal for the signal name, represented as the given enum (represented as uint8), based on the public signals.
    /// @notice Implementation must be able to handle intrinsic signals, defiend in IntrinsicSignalName enum.
    ///         Type-specific signals support is optional.
    /// @param name The signal name, represented as the given enum (converted to uint8).
    /// @param publicSignals The public signals.
    function getPublicSignal(uint8 name, uint256[] calldata publicSignals) external view returns (uint256);
}

contracts/interfaces/IStatefulVerifier.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import { IIssuerRegistry } from "./IIssuerRegistry.sol";
import { ITypeRegistry } from "./ITypeRegistry.sol";

/// @title VerifyResult
/// @notice Enum representing the possible results of a proof verification.
/// Only the OK result indicates a successful verification.
enum VerifyResult {
    OK,
    TYPE_UNINITIALIZED,
    TYPE_ID_MISMATCH,
    CONTEXT_ID_MISMATCH,
    PUBKEY_INACTIVE,
    SIG_REVOCATION_SMT_ROOT_MISMATCH,
    PROOF_INVALID,
    EXPIRED,
    UNKNOWN_ERROR
}

/// @title IBabyzkStatefulVerifier
/// @notice Interface for the BabyzkStatefulVerifier contract which handles on-chain stateful verification of zero-knowledge proofs.
interface IStatefulVerifier {
    /// @notice Emitted when the TypeRegistry is updated.
    /// @param oldTypeRegistry The address of the old TypeRegistry.
    /// @param newTypeRegistry The address of the new TypeRegistry.
    event TypeRegistryUpdated(ITypeRegistry oldTypeRegistry, ITypeRegistry newTypeRegistry);

    /// @notice Emitted when the IssuerRegistry is updated.
    /// @param oldIssuerRegistry The address of the old IssuerRegistry.
    /// @param newIssuerRegistry The address of the new IssuerRegistry.
    event IssuerRegistryUpdated(IIssuerRegistry oldIssuerRegistry, IIssuerRegistry newIssuerRegistry);

    /// @notice Performs static verification of zero-knowledge proofs.
    /// @param typeId The type ID of the proof to verify.
    /// @param contextId The context ID of the proof to verify.
    /// @param keyId The key ID associated with the proof.
    /// @param proofs An array containing the proof data.
    /// @param publicSignals An array containing the public signals data.
    /// @return A value from the VerifyResult enum representing the verification result.
    function verifyProofStatic(
        uint160 typeId,
        uint160 contextId,
        uint256 keyId,
        uint256[] calldata proofs,
        uint256[] calldata publicSignals
    ) external view returns (VerifyResult);

    /// @notice Performs full verification of zero-knowledge proofs, including issuer public key activity and revocation checks.
    /// @param typeId The type ID of the proof to verify.
    /// @param contextId The context ID of the proof to verify.
    /// @param issuerId The issuer ID associated with the proof.
    /// @param proofs An array containing the proof data.
    /// @param publicSignals An array containing the public signals data.
    /// @return A value from the VerifyResult enum representing the verification result.
    function verifyProofFull(
        uint160 typeId,
        uint160 contextId,
        uint256 issuerId,
        uint256[] calldata proofs,
        uint256[] calldata publicSignals
    ) external view returns (VerifyResult);

    /// @notice Returns the current TypeRegistry address.
    /// @return The address of the TypeRegistry.
    function getTypeRegistry() external view returns (ITypeRegistry);

    /// @notice Returns the current IssuerRegistry address.
    /// @return The address of the IssuerRegistry.
    function getIssuerRegistry() external view returns (IIssuerRegistry);
}

contracts/interfaces/ITypeRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import { IProofVerifier } from "./IProofVerifier.sol";
import { IPublicSignalGetter } from "./IPublicSignalGetter.sol";

/// @title Credential Type
/// @dev Defines the structure of a credential type.
struct CredentialType {
    // @dev This flag indicates if the credential type support revocation.
    bool revocable;
    // @dev The address of the admin of the credential type, initially set to the creator.
    address admin;
    // @dev Provides a human-readable identifier for the credential.
    string name;
    // @dev The type specification of Galxe Identity Credential Type Schema.
    string definition;
    // @dev Additional context or information about the type.
    string description;
    // @dev A URI to an external JSON file of more information about the credential.
    // See whitepaper for the JSON schema specification.
    string resourceURI;
}

/// @title Credential Type Config, miscellanous.
/// @notice To save stack space, we use this struct to store the verifier and public signal getter.
struct CredentialTypeMiscConfig {
    bool revocable;
    uint8 verificationStackId;
    IProofVerifier verifier;
    IPublicSignalGetter publicSignalGetter;
}

/// @title Interface for Type Registration Contract
/// @notice This interface defines the functions for registering and managing types by users.
interface ITypeRegistry {
    /// @dev Emitted when a new type is registered
    /// @param typeID Unique identifier for the registered type
    /// @param admin Address of the admin of the type.
    /// @param name Name of the type
    /// @param definition Immutable definition of the type
    /// @param description Immutable description of the type
    /// @param resourceURI Mutable resource URI for the type
    event TypeRegistered(
        uint160 indexed typeID,
        address indexed admin,
        string name,
        string definition,
        string description,
        string resourceURI
    );

    /// @dev Emitted when the resource URI of a type is updated
    /// @param typeID Unique identifier for the type being updated
    /// @param oldResourceURI The previous resource URI
    /// @param newResourceURI The new resource URI
    event TypeResourceURIUpdated(uint160 indexed typeID, string oldResourceURI, string newResourceURI);

    /// @dev Emitted when a proof verifier is updated for a type
    /// @param typeID type id
    /// @param verificationStackID verification stack id
    /// @param verifier address of the verifier
    event TypeVerifierUpdated(uint160 indexed typeID, uint8 indexed verificationStackID, address indexed verifier);

    /// @dev Emitted when the intrinsic signal indexes are updated for a type
    /// @param typeID type id
    /// @param verificationStackID verification stack id
    /// @param publicSignalGetter  address of the public signal getter
    event TypePublicSignalGetterUpdated(
        uint160 indexed typeID,
        uint8 indexed verificationStackID,
        address indexed publicSignalGetter
    );

    /// @dev Emitted when the admin of a type is transferred
    /// @param typeID type id
    /// @param oldAdmin address of the old admin
    /// @param newAdmin address of the new admin
    event TypeAdminTransferred(uint160 indexed typeID, address indexed oldAdmin, address indexed newAdmin);

    /// @dev transfer the ownership of a type.
    /// @param typeId type id of the type
    /// @param newAdmin address of the new admin
    function transferTypeAdmin(uint160 typeId, address newAdmin) external;

    /// @notice set a primitive type, only callable by the admin.
    /// @param typeId the type id of the primitive type
    /// @param name name of the type
    /// @param definition definition string
    /// @param description Description of the type
    /// @param resourceURI Mutable resource URI of the type
    /// @param config revocable, verifier and public signal getter.
    function setPrimitiveType(
        uint160 typeId,
        string calldata name,
        string calldata definition,
        string calldata description,
        string calldata resourceURI,
        CredentialTypeMiscConfig calldata config
    ) external;

    /// @notice Register a new type by `msg.sender`
    /// @param name Type name
    /// @param definition Immutable type definition string
    /// @param description Description of the type
    /// @param resourceURI Mutable resource URI of the type
    /// @return typeID Unique identifier of the registered type
    function registerType(
        bool revocable,
        string calldata name,
        string calldata definition,
        string calldata description,
        string calldata resourceURI
    ) external returns (uint160);

    /// @notice Register a new type by `msg.sender` with verifier and public signal getter set for a specific verification stack.
    /// @param name Type name
    /// @param definition Immutable type definition string
    /// @param description Description of the type
    /// @param resourceURI Mutable resource URI of the type
    /// @param verifier verifier for the type
    /// @param publicSignalGetter public signal getter for the type
    /// @return typeID Unique identifier of the registered type
    function registerType1Step(
        bool revocable,
        string calldata name,
        string calldata definition,
        string calldata description,
        string calldata resourceURI,
        uint8 verificationStackId,
        IProofVerifier verifier,
        IPublicSignalGetter publicSignalGetter
    ) external returns (uint160);

    /// @notice Update the resource URI of a type
    /// @dev Can only be called by the type owner (`msg.sender`)
    /// @param _id ID of the type to update
    /// @param _resourceURI New resource URI for the type
    function updateTypeResourceURI(uint160 _id, string calldata _resourceURI) external;

    /// @dev update the verifier for a type
    function updateTypeVerifier(uint160 typeId, uint8 verificationStackId, IProofVerifier verifier) external;

    /// @dev update the verifier for a type
    function updateTypePublicSignalGetter(
        uint160 typeId,
        uint8 verificationStackId,
        IPublicSignalGetter getter
    ) external;

    /// @param typeId type id of the type
    /// @param verificationStackId verification stack id
    function getVerifier(uint160 typeId, uint8 verificationStackId) external view returns (IProofVerifier);

    /// @param typeId type id of the type
    /// @param verificationStackId verification stack id
    function getPublicSignalGetter(
        uint160 typeId,
        uint8 verificationStackId
    ) external view returns (IPublicSignalGetter);

    /// @dev check if the type is fully initialized for the given verification stack.
    /// @param typeId id of the type
    /// @param verificationStackId id the verification stack
    function isTypeFullyInitializedForStack(uint160 typeId, uint8 verificationStackId) external view returns (bool);

    /// @dev Retrieve details of a type for the given typeID
    /// @param _id ID of the type to retrieve
    /// @return A `CredentialType` struct containing details of the type
    function getType(uint160 _id) external view returns (CredentialType memory);

    /// @dev Retrieve the admin of a type
    /// @param typeId type id of the type
    /// @return address of the admin of the type
    function getTypeAdmin(uint160 typeId) external view returns (address);

    /// @dev check if the type is revocable
    function isRevocable(uint160 typeId) external view returns (bool);

    /// @notice Calculate the typeID of a type, typeID is keccak256(creator, name) in uint160.
    /// @param creator Address of the type creator
    /// @param name Name of the type
    /// @return The calculated typeID based on the creator address and type name
    function calcTypeID(address creator, string calldata name) external pure returns (uint160);
}

contracts/interfaces/IUpaVerifier.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

/// Reference to a single proof in a Submission.  Used by clients to show that
/// a given proof appears in a submission which has been verified as part of
/// an aggregated proof.  Not required for single-proof submissions, since in
/// this case `submissionId == proofId`, and the `merkleProof` and `location`
/// are trivial.
struct ProofReference {
    bytes32 submissionId;
    bytes32[] merkleProof;
    /// Index into the proofs in the submission.  The sequence of proofs
    /// within the submission starts at this index.
    uint16 location;
}

/// @title IUpaVerifier interface
/// @notice Any UPA that supports this interface can be used by the AggregatedStatefulVerifier contract.
interface IUpaVerifier {
    // Checks if UPA has verified a proof that publicInputs is valid for
    // the circuit `circuitId`.
    function isVerified(uint256 circuitId, uint256[] calldata publicInputs) external view returns (bool);

    // Checks if UPA has verified a proof that publicInputs is valid for
    // the circuit `circuitId`, where the proof belongs to a multi-proof
    // submission.
    function isVerified(
        uint256 circuitId,
        uint256[] calldata publicInputs,
        ProofReference calldata proofReference
    ) external view returns (bool);
}

contracts/IssuerRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import { IIssuerRegistry, Issuer, PublicKey, PublicKeyStatus, SignatureState } from "./interfaces/IIssuerRegistry.sol";

// IssuerRegistry is a contract that manages the issuers and their public keys and signature state.
contract IssuerRegistry is IIssuerRegistry {
    error IssuerAlreadyExists();
    error IssuerNotExists();
    error PublicKeyNotExists();
    error PublicKeyAlreadyExists();
    error NotIssuerOwner(address owner);
    error NotOwner();
    error InvalidName();

    /// @dev all issuers, isser id = uint256(caller)
    // mapping issuer id to issuer.
    mapping(uint256 isserId => Issuer issuer) private _issuers;

    // (issuerId, publickeyId) => PublicKey
    mapping(uint256 issuerId => mapping(uint256 publicKeyId => PublicKey publicKey)) private _issuerPublicKeys;

    // mapping of hash(credentialType, contextId, issuerId) => SignatureState.
    mapping(uint256 credHashValue => SignatureState sigStatus) private _revocableSigStates;

    /// @dev only issuer owner can call this function
    modifier onlyIssuerAdmin(uint256 issuerId) {
        _onlyIssuerAdmin(issuerId);
        _;
    }

    /// @dev only issuer owner can call this function
    function _onlyIssuerAdmin(uint256 issuerId) private view {
        if (!_issuerExists(issuerId)) {
            revert IssuerNotExists();
        }
        if (msg.sender != _issuers[issuerId].admin) {
            revert NotIssuerOwner(_issuers[issuerId].admin);
        }
    }

    /// @dev register a new issuer
    // @notice the issuerId is the address of the caller, so 1 address can only have 1 issuer.
    function registerIssuer(
        string calldata name,
        uint8 verificationStackId,
        uint256 publicKeyId,
        bytes calldata publicKeyRaw
    ) external override returns (uint256) {
        uint256 issuerId = uint256(uint160(msg.sender));
        // register new issuer
        _registerNewIssuer(issuerId, name);
        // add public key
        _setPublicKey(issuerId, verificationStackId, publicKeyId, publicKeyRaw);
        // transfer ownership
        return issuerId;
    }

    /// @dev transfer the admin of the issuer
    function transferIssuerAdmin(uint256 issuerId, address newOwner) external override onlyIssuerAdmin(issuerId) {
        _issuers[issuerId].admin = newOwner;
        emit IssuerAdminTransferred(issuerId, msg.sender, newOwner);
    }

    /// @dev add a new public key to the issuer.
    // @param issuerId the id of the issuer
    // @param verificationStackId the id of the verification stack
    // @param publicKeyId the id of the public key
    // @param publicKeyRaw the raw public key
    // @notice only the issuer admin can call this function
    // @notice the public key can only be added once, if incorrect, use a new key.
    function addPublicKey(
        uint256 issuerId,
        uint8 verificationStackId,
        uint256 publicKeyId,
        bytes calldata publicKeyRaw
    ) external override onlyIssuerAdmin(issuerId) {
        if (_publicKeyExists(issuerId, publicKeyId)) {
            revert PublicKeyAlreadyExists();
        }
        _setPublicKey(issuerId, verificationStackId, publicKeyId, publicKeyRaw);
    }

    /// @dev update the status of the public key.
    function updatePublicKeyStatus(
        uint256 issuerId,
        uint256 publicKeyId,
        PublicKeyStatus status
    ) external override onlyIssuerAdmin(issuerId) {
        if (!_publicKeyExists(issuerId, publicKeyId)) {
            revert PublicKeyNotExists();
        }

        _issuerPublicKeys[issuerId][publicKeyId].status = status;
        emit PublicKeyStatusUpdated(issuerId, publicKeyId, status);
    }

    /// @dev update the status of the public key.
    function updatePublicKeyVerificationStack(
        uint256 issuerId,
        uint256 publicKeyId,
        uint8 verificationStackId,
        bool enabled
    ) external override onlyIssuerAdmin(issuerId) {
        if (!_publicKeyExists(issuerId, publicKeyId)) {
            revert PublicKeyNotExists();
        }

        _issuerPublicKeys[issuerId][publicKeyId].enabledVerificationStacks[verificationStackId] = enabled;
        emit PublicKeyVerificationStackUpdated(issuerId, publicKeyId, verificationStackId, enabled);
    }

    /// @dev set the signature state URI
    function updateSignatureStateURI(
        uint160 typeId,
        uint160 contextId,
        uint256 issuerId,
        string calldata treeURI
    ) external override onlyIssuerAdmin(issuerId) {
        uint256 key = _calculateCredentialHash(typeId, contextId, issuerId);
        _revocableSigStates[key].treeURI = treeURI;
        emit SignatureStateURIUpdated(typeId, contextId, issuerId, treeURI);
    }

    /// @dev set the signature state root
    function updateSignatureState(
        uint160 typeId,
        uint160 contextId,
        uint256 issuerId,
        bytes32 root
    ) external override onlyIssuerAdmin(issuerId) {
        uint256 key = _calculateCredentialHash(typeId, contextId, issuerId);
        _revocableSigStates[key].root = root;
        emit SignatureStateRootUpdated(typeId, contextId, issuerId, root);
    }

    /// @dev set both the signature state URI and root.
    function setSignatureState(
        uint160 typeId,
        uint160 contextId,
        uint256 issuerId,
        string calldata treeURI,
        bytes32 root
    ) external override onlyIssuerAdmin(issuerId) {
        uint256 key = _calculateCredentialHash(typeId, contextId, issuerId);
        _revocableSigStates[key] = SignatureState({ treeURI: treeURI, root: root });
        emit SignatureStateURIUpdated(typeId, contextId, issuerId, treeURI);
        emit SignatureStateRootUpdated(typeId, contextId, issuerId, root);
    }

    /// @dev calculate the hash of the credential, which is the key of the revocableSigStates.
    function _calculateCredentialHash(
        uint160 typeId,
        uint160 contextId,
        uint256 issuerId
    ) private pure returns (uint256) {
        return uint256(keccak256(abi.encodePacked(typeId, contextId, issuerId)));
    }

    /// @dev register a new issuer
    // @param issuerId the id of the issuer
    // @param name the name of the issuer
    function _registerNewIssuer(uint256 issuerId, string calldata name) private {
        if (bytes(name).length == 0) {
            revert InvalidName();
        }
        if (_issuerExists(issuerId)) {
            revert IssuerAlreadyExists();
        }

        _issuers[issuerId] = Issuer({ name: name, admin: msg.sender });
        emit IssuerRegistered(issuerId, name);
        emit IssuerAdminTransferred(issuerId, address(0), msg.sender);
    }

    /// @dev add a new public key to the issuer
    // @param issuerId the id of the issuer
    // @param verificationStackId the id of the verification stack
    // @param publicKeyId the id of the public key
    // @param publicKeyRaw the raw public key
    function _setPublicKey(
        uint256 issuerId,
        uint8 verificationStackId,
        uint256 publicKeyId,
        bytes calldata publicKeyRaw
    ) private {
        mapping(uint256 => PublicKey) storage keys = _issuerPublicKeys[issuerId];
        keys[publicKeyId].enabledVerificationStacks[verificationStackId] = true;
        keys[publicKeyId].status = PublicKeyStatus.ACTIVE;
        keys[publicKeyId].raw = publicKeyRaw;

        emit PublicKeyStatusUpdated(issuerId, publicKeyId, PublicKeyStatus.ACTIVE);
        emit PublicKeyVerificationStackUpdated(issuerId, publicKeyId, verificationStackId, true);
    }

    /// @dev return the issuer, if not exists, revert.
    function getIssuer(uint256 issuerId) external view override returns (Issuer memory) {
        if (!_issuerExists(issuerId)) {
            revert IssuerNotExists();
        }
        return _issuers[issuerId];
    }

    /// @dev return the public key, if not exists, revert.
    function getPublicKeyRaw(uint256 issuerId, uint256 publicKeyId) external view override returns (bytes memory) {
        if (!_publicKeyExists(issuerId, publicKeyId)) {
            revert PublicKeyNotExists();
        }
        return _issuerPublicKeys[issuerId][publicKeyId].raw;
    }

    /// @dev return if public key is active. If public key is not exists, return false.
    function isPublicKeyActive(uint256 issuerId, uint256 publicKeyId) external view override returns (bool) {
        if (!_publicKeyExists(issuerId, publicKeyId)) {
            return false;
        }
        return _issuerPublicKeys[issuerId][publicKeyId].status == PublicKeyStatus.ACTIVE;
    }

    /// @dev return if public key is active for the verification stack. If public key is not exists, return false.
    function isPublicKeyActiveForStack(
        uint256 issuerId,
        uint256 publicKeyId,
        uint8 verificationStackId
    ) external view override returns (bool) {
        if (!_publicKeyExists(issuerId, publicKeyId)) {
            return false;
        }
        return
            _issuerPublicKeys[issuerId][publicKeyId].enabledVerificationStacks[verificationStackId] &&
            _issuerPublicKeys[issuerId][publicKeyId].status == PublicKeyStatus.ACTIVE;
    }

    /// @dev return the status of the public key, if not exists, returns UNINITIALIZED.
    function getSignatureState(
        uint160 typeId,
        uint160 contextId,
        uint256 issuerId
    ) external view override returns (SignatureState memory) {
        return _revocableSigStates[_calculateCredentialHash(typeId, contextId, issuerId)];
    }

    /// @dev return the status of the public key, if not exists, returns empty string.
    function getSignatureStateURI(
        uint160 typeId,
        uint160 contextId,
        uint256 issuerId
    ) external view override returns (string memory) {
        return _revocableSigStates[_calculateCredentialHash(typeId, contextId, issuerId)].treeURI;
    }

    /// @dev return the root of the signature state, if not exists, returns empty bytes.
    function getSignatureStateRoot(
        uint160 typeId,
        uint160 contextId,
        uint256 issuerId
    ) external view override returns (bytes32) {
        return _revocableSigStates[_calculateCredentialHash(typeId, contextId, issuerId)].root;
    }

    /// @dev internal function to check if the public key exists.
    function _publicKeyExists(uint256 issuerId, uint256 publicKeyId) internal view returns (bool) {
        return _issuerPublicKeys[issuerId][publicKeyId].status != PublicKeyStatus.UNINITIALIZED;
    }

    /// @dev internal function to check if the issuer exists.
    function _issuerExists(uint256 issuerId) internal view returns (bool) {
        return bytes(_issuers[issuerId].name).length != 0;
    }
}

contracts/TypeRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

import { ITypeRegistry, CredentialType, CredentialTypeMiscConfig } from "./interfaces/ITypeRegistry.sol";
import { IProofVerifier } from "./interfaces/IProofVerifier.sol";
import { IPublicSignalGetter } from "./interfaces/IPublicSignalGetter.sol";

/**
 * @title TypeRegistry
 * @dev A contract that allows the registration of credential types.
 *      Credential types are unique per creator and type name.
 * @notice Getters in this contract does not do any parameter validation,
 *         so callers should validate the inputs and outputs and uninitialized values will be zero.
 */
contract TypeRegistry is ITypeRegistry, Ownable {
    error TypeAlreadyExists();
    error TypeDoesNotExist();
    error InvalidTypeName();
    error NotTypeOwner();

    /// @dev mapping from typeID to CredentialType.
    mapping(uint160 typeId => CredentialType credType) private _credTypes;

    /// @dev mapping from typeID to its verification stack's verifier.
    /// @dev verifier typeID => verificationStackID => verifier
    mapping(uint160 typeId => mapping(uint8 veriStackId => IProofVerifier verifier)) private _verifiers;

    /// @dev mapping from typeID to its verification stack's intrinsic value .
    /// @dev verifier typeID => verificationStackID => verifier
    mapping(uint160 typeId => mapping(uint8 veriStackId => IPublicSignalGetter psGetter)) private _psGetters;

    /// @param admin admin of the contract
    constructor(address admin) {
        transferOwnership(admin);
    }

    /// @notice set a primitive type, only callable by the admin.
    /// @param typeId the type id of the primitive type
    /// @param name name of the type
    /// @param definition definition string
    /// @param description description of the type
    /// @param resourceURI mutable resource URI of the type
    /// @param config misc config for the type
    function setPrimitiveType(
        uint160 typeId,
        string calldata name,
        string calldata definition,
        string calldata description,
        string calldata resourceURI,
        CredentialTypeMiscConfig calldata config
    ) external override onlyOwner {
        // check if the type is already set by others.
        // primitive type will have its creator as 0x0.
        if (bytes(_credTypes[typeId].name).length != 0) {
            revert TypeAlreadyExists();
        }
        if (bytes(name).length == 0) {
            revert InvalidTypeName();
        }
        // save type info
        _credTypes[typeId] = CredentialType({
            revocable: config.revocable,
            admin: address(0),
            name: name,
            definition: definition,
            description: description,
            resourceURI: resourceURI
        });
        emit TypeRegistered(typeId, address(0), name, definition, description, resourceURI);
        // add verifier and public signal getter
        _verifiers[typeId][config.verificationStackId] = config.verifier;
        emit TypeVerifierUpdated(typeId, config.verificationStackId, address(config.verifier));
        _psGetters[typeId][config.verificationStackId] = config.publicSignalGetter;
        emit TypePublicSignalGetterUpdated(typeId, config.verificationStackId, address(config.publicSignalGetter));
    }

    /// @dev register a new type by msg.sender
    /// @param name type name
    /// @param definition type definition string, immutable
    /// @param description description of the type, immutable
    /// @param resourceURI resource URI of the type, mutable
    /// @return typeID of the registered type
    function registerType(
        bool revocable,
        string calldata name,
        string calldata definition,
        string calldata description,
        string calldata resourceURI
    ) external override returns (uint160) {
        return _registerType(revocable, name, definition, description, resourceURI);
    }

    /// @notice Register a new type by `msg.sender` with verifier and public signal getter set for a specific verification stack.
    /// @param name Type name
    /// @param definition Immutable type definition string
    /// @param description Description of the type
    /// @param resourceURI Mutable resource URI of the type
    /// @param verifier verifier for the type
    /// @param publicSignalGetter public signal getter for the type
    /// @return typeID Unique identifier of the registered type
    function registerType1Step(
        bool revocable,
        string calldata name,
        string calldata definition,
        string calldata description,
        string calldata resourceURI,
        uint8 verificationStackId,
        IProofVerifier verifier,
        IPublicSignalGetter publicSignalGetter
    ) external override returns (uint160) {
        uint160 typeId = _registerType(revocable, name, definition, description, resourceURI);
        // add verifier and public signal getter
        _verifiers[typeId][verificationStackId] = verifier;
        emit TypeVerifierUpdated(typeId, verificationStackId, address(verifier));
        _psGetters[typeId][verificationStackId] = publicSignalGetter;
        emit TypePublicSignalGetterUpdated(typeId, verificationStackId, address(publicSignalGetter));
        return typeId;
    }

    /// @dev register a new type by msg.sender
    /// @param name type name
    /// @param definition type definition string, immutable
    /// @param description description of the type, immutable
    /// @param resourceURI resource URI of the type, mutable
    /// @return typeID of the registered type
    function _registerType(
        bool revocable,
        string calldata name,
        string calldata definition,
        string calldata description,
        string calldata resourceURI
    ) internal returns (uint160) {
        uint160 credTypeID = _calcTypeID(msg.sender, name);
        if (bytes(_credTypes[credTypeID].name).length != 0) {
            revert TypeAlreadyExists();
        }
        if (bytes(name).length == 0) {
            revert InvalidTypeName();
        }
        CredentialType memory credType = CredentialType({
            revocable: revocable,
            admin: msg.sender,
            name: name,
            definition: definition,
            description: description,
            resourceURI: resourceURI
        });
        _credTypes[credTypeID] = credType;
        emit TypeRegistered(
            credTypeID,
            credType.admin,
            credType.name,
            credType.definition,
            credType.description,
            credType.resourceURI
        );
        return credTypeID;
    }

    /// @dev check if the type is fully initialized for the given verification stack.
    /// @param typeId id of the type
    /// @param verificationStackId id the verification stack
    function isTypeFullyInitializedForStack(
        uint160 typeId,
        uint8 verificationStackId
    ) external view override returns (bool) {
        return
            _typeExists(typeId) &&
            _verifiers[typeId][verificationStackId] != IProofVerifier(address(0)) &&
            _psGetters[typeId][verificationStackId] != IPublicSignalGetter(address(0));
    }

    /// @dev transfer the ownership of a type.
    /// @param typeId type id of the type
    /// @param newAdmin address of the new admin
    function transferTypeAdmin(uint160 typeId, address newAdmin) external override onlyTypeOwner(typeId) {
        if (_credTypes[typeId].admin == address(0)) {
            // primitive type doesn't have an admin, so the owner can't transfer it.
            return;
        }
        _credTypes[typeId].admin = newAdmin;
        emit TypeAdminTransferred(typeId, msg.sender, newAdmin);
    }

    /// @dev update the resource URI of a type
    /// @param typeId of the type
    /// @param uri new resource URI
    /// TODO: resource URI specifcation: support multiple verification stack.
    function updateTypeResourceURI(uint160 typeId, string calldata uri) external override onlyTypeOwner(typeId) {
        emit TypeResourceURIUpdated(typeId, _credTypes[typeId].resourceURI, uri);
        _credTypes[typeId].resourceURI = uri;
    }

    /// @dev update the verifier for a type
    function updateTypeVerifier(
        uint160 typeId,
        uint8 verificationStackId,
        IProofVerifier verifier
    ) external override onlyTypeOwner(typeId) {
        _verifiers[typeId][verificationStackId] = verifier;
        emit TypeVerifierUpdated(typeId, verificationStackId, address(verifier));
    }

    /// @dev update the public signal getter for a type
    /// @param typeId id of the type
    /// @param verificationStackId id of the verification stack
    /// @param getter the address of the public signal getter
    function updateTypePublicSignalGetter(
        uint160 typeId,
        uint8 verificationStackId,
        IPublicSignalGetter getter
    ) external override onlyTypeOwner(typeId) {
        _psGetters[typeId][verificationStackId] = getter;
        emit TypePublicSignalGetterUpdated(typeId, verificationStackId, address(getter));
    }

    /// @dev get the type for the given typeID
    function getType(uint160 _id) external view override returns (CredentialType memory) {
        return _credTypes[_id];
    }

    /// @dev Retrieve the admin of a type
    /// @param typeId type id of the type
    /// @return address of the admin of the type
    function getTypeAdmin(uint160 typeId) external view override returns (address) {
        /// uninitialized type, return 0x0
        if (!_typeExists(typeId)) {
            return address(0);
        }
        if (_credTypes[typeId].admin != address(0)) {
            return _credTypes[typeId].admin;
        }
        return owner();
    }

    /// @dev check if the type is revocable
    function isRevocable(uint160 typeId) external view override returns (bool) {
        return _credTypes[typeId].revocable;
    }

    /// @dev get the verifier for the given typeID and verificationStackID.
    function getVerifier(uint160 typeId, uint8 verificationStackId) external view override returns (IProofVerifier) {
        return _verifiers[typeId][verificationStackId];
    }

    /// @param typeId type id of the type
    /// @param verificationStackId verification stack id
    function getPublicSignalGetter(
        uint160 typeId,
        uint8 verificationStackId
    ) external view override returns (IPublicSignalGetter) {
        return _psGetters[typeId][verificationStackId];
    }

    /// @dev calculate the typeID of a type
    function calcTypeID(address creator, string calldata name) external pure override returns (uint160) {
        return _calcTypeID(creator, name);
    }

    /// @dev calculate the typeID of a type
    function _calcTypeID(address creator, string calldata name) private pure returns (uint160) {
        return uint160(uint256(keccak256(abi.encodePacked(creator, name))));
    }

    /// @dev check if the type exists
    function _typeExists(uint160 typeId) private view returns (bool) {
        return bytes(_credTypes[typeId].name).length != 0;
    }

    /// @dev check if the caller is the owner of the type
    function _onlyTypeOwner(uint160 typeId) private view {
        if (!_typeExists(typeId)) {
            revert TypeDoesNotExist();
        }
        // primitive type will have its creator as 0x0, so only the admin can update it.
        if (_credTypes[typeId].admin == address(0)) {
            if (msg.sender != owner()) {
                revert NotTypeOwner();
            }
            return;
        }
        if (_credTypes[typeId].admin != msg.sender) {
            revert NotTypeOwner();
        }
    }

    modifier onlyTypeOwner(uint160 typeId) {
        _onlyTypeOwner(typeId);
        _;
    }
}
