                revert NotTypeOwner();
            }
            return;
        }
        if (_credTypes[typeId].admin != msg.sender) {
            revert NotTypeOwner();
        }
    }

    modifier onlyTypeOwner(uint160 typeId) {
        _onlyTypeOwner(typeId);
        _;
    }
}
